$_MODULE['<{wic_erp}prestashop>informations_c4c95c36570d5a8834be5e88e2f0f6b2'] = 'Informations';
$_MODULE['<{wic_erp}prestashop>informations_44fa3d9acb1fdc7c5db3bf265b452c14'] = 'My Easy ERP';
$_MODULE['<{wic_erp}prestashop>informations_a6bddb4a42ba01f427fad04eb13d9595'] = 'Ce module My Easy ERP pour Prestashop s\'adresse à tous les e­commerçants souhaitant répondre efficacement aux problématiques de gestion logistique et de gestion de stocks (état de stocks) avec une solution ERP embarquée, complète et performante, simple à configurer et à moindre coût.';
$_MODULE['<{wic_erp}prestashop>informations_078fec209252573eab364bb9399acf16'] = 'Ce module est le fruit de nos nombreuses années d\'expérience des problématiques logistiques et de gestion de stocks liées au e­commerce et appliquées à la technologie Prestashop sur laquelle nous sommes agence certifiée.';
$_MODULE['<{wic_erp}prestashop>informations_7eb6425ae30177d72ecd92eba116e0b8'] = 'Notre but est de proposer un module qui réponde aux problématiques logistiques du plus grand nombre de commerçants sous Prestashop tout en offrant des fonctionnalités avancées si nécessaire. Vous gagnerez énormément de temps et d’argent dans la gestion au quotidien de votre stock, de vos réapprovisionnement, de vos commandes fournisseurs.';
$_MODULE['<{wic_erp}prestashop>informations_c489b3dc46922699ea8bdfd58e525a1e'] = 'Le module offre la possibilité de gérer votre stock de façon classique et / ou en flux tendus.';
$_MODULE['<{wic_erp}prestashop>informations_1c5501f0d9dc8516d24c37365afe6ab4'] = 'Dans le cas d’une configuration classique le module permet de gérer les problématiques de réapprovisionnement automatiquement et de plusieurs façons : selon la “loi normal” (seuil de réapprovisionnement calculé selon le seuil de risque de rupture), en mode expert ou en les forçant manuellement.';
$_MODULE['<{wic_erp}prestashop>informations_54b795da4dbb6dd1f487427a9c0cdcb7'] = 'La configuration des fournisseurs et des produits (seuil, quantité, délais de livraison, unité minimum par produits) peut être effectuée au niveau global et automatisée, ou spécifiée de façon plus précise par fournisseur puis par produits.';
$_MODULE['<{wic_erp}prestashop>informations_7a40cd284f35118ec7f2992461975d8b'] = 'Enfin toutes les mises à jour d’états de stocks et les mouvement de stocks associés sont effectuées en TEMPS RÉEL.';
$_MODULE['<{wic_erp}prestashop>informations_344aeecad7c914f68e0eda60c5b78952'] = 'Ce module permet d’intégrer la fonctionnalité My Easy ERP sur votre site Prestashop en quelques clics :';
$_MODULE['<{wic_erp}prestashop>informations_82d968b0e664bb283517297945b41893'] = 'Intégration complète dans votre administration de modules, installation en un clic,';
$_MODULE['<{wic_erp}prestashop>informations_02347e9eea9394ba4d3ad46a4de9c3f0'] = 'Configuration du module simple depuis votre Back­Office Prestashop,';
$_MODULE['<{wic_erp}prestashop>informations_92f7996cf006fea3dbfb7dc2b20f4c07'] = 'Mode de gestion du stock de type Classique avec stock “In House”,';
$_MODULE['<{wic_erp}prestashop>informations_eb0ee807569561ba27f93fe0962fa62e'] = 'Mode de gestion du stock de type Flux tendus,';
$_MODULE['<{wic_erp}prestashop>informations_a3eca57efa6c54568862721fe2c3e5bc'] = 'Gestion des commandes auprès des fournisseurs,';
$_MODULE['<{wic_erp}prestashop>informations_b67bdfc784619e4384b849bd95de707b'] = 'Gestion du réapprovisionnement au niveau produit : points de commande (quantité à partir de laquelle la commande doit être lancée), stock de sécurité, stock minimum, délais de réapprovisionnement par produit et par fournisseur, quantités minimum par références et unités de commandes,';
$_MODULE['<{wic_erp}prestashop>informations_09bb54e747e00901e6ae9c3951d1283f'] = 'Mode de gestion expert ou selon la loi normale : calcul des données de réapprovisionnement en fonction des délais de livraison fournisseurs et des statistiques de ventes sur une période donnée (configurable) ou selon la loi normale avec calcul de risque, analyse prédictive des ventes,';
